#!/bin/bash

helpFunction()
{
   echo "Mask a GISAID alignment assuming coordinates are w.r.t. WH1"
   echo "Usage: $0 -i fasta_file_of_SARS-CoV-2_seqs -o output_file -t threads"
   echo "\t-i Full path to input alignment of SARS-CoV-2 sequences"
   echo "\t-o Output file path of masked alignment"
   echo "\t-t number of threads to use"
   exit 1 # Exit script after printing help
}

while getopts "i:o:t:" opt
do
   case "$opt" in
      i ) inputfasta="$OPTARG" ;;
      o ) outputfasta="$OPTARG" ;;
      t ) threads="$OPTARG" ;;
      ? ) helpFunction ;; # Print helpFunction in case parameter is non-existent
   esac
done

# Print helpFunction in case parameters are empty
if [ -z "$inputfasta" ] || [ -z "$outputfasta" ] || [ -z "$threads" ]
then
   echo "Some or all of the parameters are empty";
   helpFunction
fi


# download latest masking file and extract the sites to mask
wget https://raw.githubusercontent.com/W-L/ProblematicSites_SARS-CoV2/master/subset_vcf/problematic_sites_sarsCov2.mask.vcf
mask_sites=$(bcftools query -f '%POS\t' problematic_sites_sarsCov2.mask.vcf)
export MASK_SITES=$mask_sites

# TODO: switch this back to just conda when v 0.3.3c is available
# we also need the latest (non-conda) version of goalign for this
wget https://github.com/evolbioinfo/goalign/releases/download/v0.3.3c/goalign_amd64_linux
chmod +x goalign_amd64_linux

echo "Masking all sites suggested here: https://raw.githubusercontent.com/W-L/ProblematicSites_SARS-CoV2/master/subset_vcf/problematic_sites_sarsCov2.mask.vcf"


# first we copy the input
cp $inputfasta $outputfasta

# then we add the reference we want
echo ">reference_sequence" >> $outputfasta
echo "" >> $outputfasta


# now we mask each site in turn
for site in $MASK_SITES; do

   # goalign works on slice indices, not site indices, so watch out...
   # e.g. goalign mask -l 1 -s 2 from seq AAA will give AAN (mask of length 1 from second slice)
   start=$(($site-1))

   ./goalign_amd64_linux mask -i $outputfasta -l 1 -s $start --ref-seq 'reference_sequence' -o $outputfasta".mask"
   
   # cycle the file names so we can keep updating the alignment, not a beautiful solution :(
   rm $outputfasta
   mv $outputfasta".mask" $outputfasta

done

